export function getSpaLocation(citySlug: string, businessTypeSlug: string): SpaLocation | undefined {
    return spaLocationsData.find(
        (location) => location.citySlug === citySlug && location.businessTypeSlug === businessTypeSlug
    );
}

export function getAllSpaSlugs(): { city: string; businessType: string }[] {
    return spaLocationsData.map((location) => ({
        city: location.citySlug,
        businessType: location.businessTypeSlug,
    }));
}

export function getSpasByCity(citySlug: string): SpaLocation[] {
    return spaLocationsData.filter((location) => location.citySlug === citySlug);
}

export function getSpasByType(businessTypeSlug: string): SpaLocation[] {
    return spaLocationsData.filter((location) => location.businessTypeSlug === businessTypeSlug);
}

export function getAllCities(): Array<{ name: string; slug: string; country: string }> {
    const cities = new Map<string, { name: string; slug: string; country: string }>();
    spaLocationsData.forEach((location) => {
        if (!cities.has(location.citySlug)) {
            cities.set(location.citySlug, {
                name: location.city,
                slug: location.citySlug,
                country: location.country
            });
        }
    });
    return Array.from(cities.values());
}

export function getAllBusinessTypes(): Array<{ name: string; slug: string }> {
    const types = new Map<string, { name: string; slug: string }>();
    spaLocationsData.forEach((location) => {
        if (!types.has(location.businessTypeSlug)) {
            types.set(location.businessTypeSlug, {
                name: location.businessType,
                slug: location.businessTypeSlug
            });
        }
    });
    return Array.from(types.values());
}

// New functions for slug-based routing
export function getSpaBySlug(slug: string): SpaLocation | undefined {
    // Parse slug like "salt-cave-spa-in-dubai"
    const parts = slug.split('-in-');
    if (parts.length !== 2) return undefined;

    const businessTypeSlug = parts[0];
    const citySlug = parts[1];

    return getSpaLocation(citySlug, businessTypeSlug);
}

export function getAllSpaPageSlugs(): { slug: string }[] {
    return spaLocationsData.map((location) => ({
        slug: `${location.businessTypeSlug}-in-${location.citySlug}`
    }));
}
